import React, { Component } from "react";
import ReactPlayer from "react-player";
import "./panel.css";

class VideoItem extends Component {
  state = {};

  render() {
    return (
      <div className="viewer-dim viewer-border viewer-color">
        <div className="video-titles">
          <h4 className="title title-position-left">{this.props.info.title}</h4>
          <h4 className="title title-position-right">
            {this.getViews(this.props.info.views)}
          </h4>
        </div>
        <div>
          <ReactPlayer
            url={this.props.info.url}
            config={{
              youtube: {
                playerVars: {
                  modestbranding: 1,
                  controls: 1,
                  showinfo: 0
                }
              },
              facebook: {
                appId: "420240272057124"
              }
            }}
          />
        </div>
      </div>
    );
  }

  getViews(views) {
    console.log(views);
    //const { views } = this.state;
    var si = [
      { value: 1, symbol: "" },
      { value: 1e3, symbol: "k" },
      { value: 1e6, symbol: "M" },
      { value: 1e9, symbol: "G" },
      { value: 1e12, symbol: "T" },
      { value: 1e15, symbol: "P" },
      { value: 1e18, symbol: "E" }
    ];
    var rx = /\.0+$|(\.[0-9]*[1-9])0+$/; //TODO: check about regex!!
    var i;
    for (i = si.length - 1; i > 0; i--) {
      if (views >= si[i].value) {
        break;
      }
    }
    return (views / si[i].value).toFixed(2).replace(rx, "$1") + si[i].symbol;
  }
}

export default VideoItem;
