import React, { Component } from "react";
import axios from "axios";

const api = "http://localhost:8080/api";

class Filter extends Component {
  state = {
    checkedCategory: 0
  };
  render() {
    return (
      <div
        style={{
          textAlign: "center",
          margin: "0 auto",
          width: "80%",
          marginTop: "10px"
        }}
      >
        <span>FILTER BY: </span>
        {this.props.categories.map((category, i) => {
          return (
            <label key={i}>
              <input
                type="radio"
                value={category}
                name="filter-group"
                checked={this.isChecked(i)}
                onChange={event => this.getFilteredData(i, event)}
                //defaultChecked={this.state.checkedCategory}
              />
              {category.toUpperCase()}
            </label>
          );
        })}
      </div>
    );
  }

  isChecked(categoryId) {
    return categoryId == this.state.checkedCategory;
  }

  getFilteredData(categoryId, event) {
    this.setState({
      checkedCategory: categoryId
    });
    axios
      .get(api + "/filter?source=" + this.props.categories[categoryId])
      .then(response => {
        this.handleListChange(response.data);
      });
  }
}

export default Filter;
