import React, { Component } from "react";
import Title from "../pageTitle/title";
import VideoList from "../videoList/videoList";
import Filter from "../filter/filter";
import axios from "axios";

const api = "http://localhost:8080/api";

class Landing extends Component {
  constructor(props) {
    super(props);

    this.state = {
      filterCategories: ["all", "facebook", "youtube"],
      videos: {}
    };
  }

  render() {
    return (
      <React.Fragment>
        <Title text="video feed" />
        <Filter
          categories={this.state.filterCategories}
          updateList={this.handleListChange}
          items={this.state.videos}
        />
        <VideoList items={this.state.videos} />
      </React.Fragment>
    );
  }

  componentDidMount() {
    axios.get(api + "/getVideos").then(respose => {
      this.setState({
        videos: respose.data
      });
    });
  }

  handleListChange = videoList => {
    this.setState(
      {
        videos: videoList
      },
      function() {
        console.log(this.state.videos);
      }
    );
  };
}

export default Landing;
