import React, { Component } from "react";

class Filter extends Component {
  state = {};
  render() {
    return (
      <div style={{ display: "inline-block", margin: "0 auto" }}>
        {this.props.categories.map((category, i) => {
          return (
            <label>
              <input
                type="radio"
                value={category}
                key={i}
                name="filter-group"
              />
              {category}
            </label>
          );
        })}
      </div>
    );
  }
}

export default Filter;
