import React, { Component } from "react";

class Filter extends Component {
  state = {
    checkedCategory: 0
  };
  render() {
    return (
      <div
        style={{
          textAlign: "center",
          margin: "0 auto",
          width: "80%",
          marginTop: "10px"
        }}
      >
        <span>FILTER BY: </span>
        {this.props.categories.map((category, i) => {
          return (
            <label>
              <input
                type="radio"
                value={category}
                key={i}
                name="filter-group"
                checked={this.isChecked(i)}
                onChange={this.getFilteredData(i)}
              />
              {category.toUpperCase()}
            </label>
          );
        })}
      </div>
    );
  }

  isChecked(categoryId) {
    return categoryId == this.state.checkedCategory;
  }
}

export default Filter;
